const l=JSON.parse('{"key":"v-ccd2ef5e","path":"/posts/mobile/%E5%83%8F%E7%B4%A0%E3%80%81%E8%A7%86%E5%8F%A3%E3%80%81%E7%BD%91%E9%A1%B5%E8%AE%A1%E9%87%8F%E5%8D%95%E4%BD%8D%E4%B8%8E%E7%A7%BB%E5%8A%A8%E7%AB%AF%E9%80%82%E9%85%8D%E6%96%B9%E6%A1%88.html","title":"像素、视口、网页计量单位与移动端适配方案","lang":"zh-CN","frontmatter":{"date":"2023-09-15T00:00:00.000Z","category":["mobile"],"tag":["mobile"]},"headers":[{"level":2,"title":"像素的概念","slug":"像素的概念","link":"#像素的概念","children":[{"level":3,"title":"设备像素（物理像素）","slug":"设备像素-物理像素","link":"#设备像素-物理像素","children":[]},{"level":3,"title":"设备独立像素","slug":"设备独立像素","link":"#设备独立像素","children":[]},{"level":3,"title":"设备像素比dpr","slug":"设备像素比dpr","link":"#设备像素比dpr","children":[]},{"level":3,"title":"像素密度PPI","slug":"像素密度ppi","link":"#像素密度ppi","children":[]},{"level":3,"title":"CSS像素","slug":"css像素","link":"#css像素","children":[]}]},{"level":2,"title":"网页中的计量单位","slug":"网页中的计量单位","link":"#网页中的计量单位","children":[{"level":3,"title":"px","slug":"px","link":"#px","children":[]},{"level":3,"title":"rem","slug":"rem","link":"#rem","children":[]},{"level":3,"title":"em","slug":"em","link":"#em","children":[]},{"level":3,"title":"vw、vh","slug":"vw、vh","link":"#vw、vh","children":[]},{"level":3,"title":"%","slug":"","link":"#","children":[]}]},{"level":2,"title":"视口viewport","slug":"视口viewport","link":"#视口viewport","children":[{"level":3,"title":"布局视口layout viewport","slug":"布局视口layout-viewport","link":"#布局视口layout-viewport","children":[]},{"level":3,"title":"视觉视口visual viewport","slug":"视觉视口visual-viewport","link":"#视觉视口visual-viewport","children":[]},{"level":3,"title":"理想视口ideal viewport","slug":"理想视口ideal-viewport","link":"#理想视口ideal-viewport","children":[]},{"level":3,"title":"利用meta标签设置viewport","slug":"利用meta标签设置viewport","link":"#利用meta标签设置viewport","children":[]}]},{"level":2,"title":"移动端适配方案","slug":"移动端适配方案","link":"#移动端适配方案","children":[]},{"level":2,"title":"参考文章","slug":"参考文章","link":"#参考文章","children":[]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"readingTime":{"minutes":8.01,"words":2404},"filePathRelative":"posts/mobile/像素、视口、网页计量单位与移动端适配方案.md","localizedDate":"2023年9月15日","excerpt":"<h1> 像素、视口、网页计量单位与移动端适配方案</h1>\\n<h2> 像素的概念</h2>\\n<h3> 设备像素（物理像素）</h3>\\n<p>指设备能控制显示的最小物理单位，像一个个小点，每一个小像素点都是由三原色RGB组成。物理像素只是为了描述物理分辨率，在屏幕<strong>出厂时就已经固定</strong>了，单位是pt。【<strong>注意区分分辨率和物理分辨率，物理分辨率固定，而分辨率可变且不能超过物理分辨率</strong>】</p>\\n<blockquote>\\n<p>物理分辨率是液晶屏<strong>固有的参数不能调节</strong>，其含义是指液晶屏最高可显示的像素数，其表示方法与分辨率相同【例如，分辨率1024*768就是指横向有1024个像素点，纵向有768个像素点。】。一般来讲物理分辨率的大小可直接决定屏幕的最高分辨率，分辨率不会大于物理分辨率。</p>\\n</blockquote>"}');export{l as data};
