const e=JSON.parse('{"key":"v-ce37794c","path":"/posts/mobile/%E5%85%B3%E4%BA%8EH5%E7%A6%81%E7%94%A8window.open%E4%BB%A5%E5%8F%8A%E8%A7%A3%E5%86%B3%E6%96%B9%E6%B3%95.html","title":"关于H5禁用window.open以及解决方法","lang":"zh-CN","frontmatter":{"date":"2023-08-16T00:00:00.000Z","category":["mobile"],"tag":["mobile"]},"headers":[{"level":2,"title":"问题描述","slug":"问题描述","link":"#问题描述","children":[]},{"level":2,"title":"解决方法","slug":"解决方法","link":"#解决方法","children":[]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"readingTime":{"minutes":2,"words":601},"filePathRelative":"posts/mobile/关于H5禁用window.open以及解决方法.md","localizedDate":"2023年8月16日","excerpt":"<h1> 关于H5禁用window.open以及解决方法</h1>\\n<h2> 问题描述</h2>\\n<p>之前做了一个需求，要求判断用户在点击职位列表的时候，判断用户是否登录，若未登录则直接跳转到登录页，登陆完成后自动跳转到对应的职位详情页；若已登录则直接跳转到相应职位详情页。\\n又因为产品要求是统一打开新窗口，因此我在pc和移动端都直接使用了window.open()。我在js里写的逻辑就是拦截了a标签的默认行为，判断登陆状态和浏览器所处环境，根据条件进而跳转到不同的页面。</p>\\n<p>在上线前的测试人员没有用真机测，只是用浏览器模拟了移动端，然后上线了才发现这个问题。也因为经验的缺乏，事前并不知道由于移动端浏览器的安全机制，会无法触发非用户行为的弹出窗口事件。</p>"}');export{e as data};
