const e=JSON.parse('{"key":"v-b29c31d2","path":"/posts/mobile/%E8%A7%A3%E5%86%B3ios%E8%AE%BE%E5%A4%87%E6%B5%8F%E8%A7%88%E5%99%A8%E9%A1%B5%E9%9D%A2%E5%9B%9E%E9%80%80%E4%BD%86%E5%86%85%E5%AE%B9%E4%B8%8D%E5%88%B7%E6%96%B0%E5%AF%BC%E8%87%B4%E7%9A%84%E9%97%AE%E9%A2%98.html","title":"解决ios设备浏览器页面回退但内容不刷新导致的问题","lang":"zh-CN","frontmatter":{"date":"2023-07-31T00:00:00.000Z","category":["mobile"],"tag":["mobile"]},"headers":[{"level":2,"title":"场景描述","slug":"场景描述","link":"#场景描述","children":[]},{"level":2,"title":"解决方法","slug":"解决方法","link":"#解决方法","children":[]},{"level":2,"title":"产生原因：","slug":"产生原因","link":"#产生原因","children":[]},{"level":2,"title":"一点疑惑","slug":"一点疑惑","link":"#一点疑惑","children":[]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"readingTime":{"minutes":2.68,"words":804},"filePathRelative":"posts/mobile/解决ios设备浏览器页面回退但内容不刷新导致的问题.md","localizedDate":"2023年7月31日","excerpt":"<h1> 解决ios设备浏览器页面回退但内容不刷新导致的问题</h1>\\n<h2> 场景描述</h2>\\n<p><strong>项目情况</strong>：项目是一个php前后端不分离项目，分为移动端和pc端，但用的基本是一套代码，在每个页面中调用判断当前是否为移动端的函数去动态的加载pc或移动端所需的css、js和dom结构代码。</p>\\n<p><strong>问题描述</strong>：今天碰到的提测的一个bug是：设备在操作部分页面后点击浏览器后退按钮，会直接显示pc端的样式，需手动刷新页面才显示移动端样式，用户交互体验差。</p>\\n<p><strong>定位问题</strong>：经过排查后发现，只有ios设备在用浏览器浏览的时候会出现以上问题，用微信内置浏览器查看没有上述问题，安卓设备也没有上述问题。\\n并且后续通过打印信息，发现页面回退时并没有再触发判断是否为移动端的函数，也因此我可以将问题定位到，页面后退后，并没有触发页面刷新，而是直接走了缓存。</p>"}');export{e as data};
